  <title>Image Slider</title>
  <style>
    .slider-container {
      width: 300px;
      height: 200px;
      overflow: hidden;
      position: relative;
    }

    .slider-container img {
      width: 100%;
      height: 100%;
      position: absolute;
      opacity: 0;
      transition: opacity 0.5s ease-in-out;
    }

    .slider-container img.active {
      opacity: 1;
    }

    .arrow {
      position: absolute;
      top: 50%;
      transform: translateY(-50%);
      background-color: rgba(0, 0, 0, 0.5);
      color: white;
      border: none;
      padding: 10px;
      cursor: pointer;
    }

    .arrow.left {
      left: 10px;
    }

    .arrow.right {
      right: 10px;
    }
  </style>
</head>

<body>

  <div class="slider-container">
    <img src="./images/download.jpeg" alt="Image 1" class="active">
    <img src="data:image/jpeg;base64," alt="Image 2">
    <img src="data:image/jpeg;base64," alt="Image 3">
    <button class="arrow left" onclick="prevImage()">&#10094;</button>
    <button class="arrow right" onclick="nextImage()">&#10095;</button>
  </div>
  <a href="index.php"><button>Go Back home</button></a>

  <script>
    let currentImageIndex = 0;
    const images = document.querySelectorAll('.slider-container img');

    function showImage(index) {
      images.forEach((img, i) => {
        img.classList.remove('active');
        if (i === index) img.classList.add('active');
      });
    }

    function nextImage() {
      currentImageIndex = (currentImageIndex + 1) % images.length;
      showImage(currentImageIndex);
    }

    function prevImage() {
      currentImageIndex = (currentImageIndex - 1 + images.length) % images.length;
      showImage(currentImageIndex);
    }
  </script>
